#!/bin/bash
cd ~/sooqkabeer_production

# Create simple colored images using Python
python3 -c "
import base64
import os

# Simple 10x10 red pixel (apple)
apple_b64 = ''
# Simple 10x10 yellow pixel (banana)
banana_b64 = ''
# Simple 10x10 brown pixel (dates)
dates_b64 = ''
# Simple 10x10 orange pixel (saffron)
saffron_b64 = ''

images = [
    ('apple.jpg', apple_b64),
    ('banana.jpg', banana_b64),
    ('dates.jpg', dates_b64),
    ('saffron.jpg', saffron_b64)
]

os.makedirs('static/images/products', exist_ok=True)

for filename, b64_data in images:
    try:
        with open(f'static/images/products/{filename}', 'wb') as f:
            f.write(base64.b64decode(b64_data))
        print(f'Created: static/images/products/{filename}')
    except:
        # Fallback: create text file
        with open(f'static/images/products/{filename}', 'w') as f:
            f.write(f'Image: {filename}')
        print(f'Created text: static/images/products/{filename}')

# Create default
with open('static/images/default_product.jpg', 'w') as f:
    f.write('Default Product')
print('Created: static/images/default_product.jpg')

# Create banners
for i in [1, 2]:
    with open(f'static/images/banner{i}.jpg', 'w') as f:
        f.write(f'Banner {i}')
    print(f'Created: static/images/banner{i}.jpg')
"
